import React, { useState, FunctionComponent, useEffect, InputHTMLAttributes, useRef } from 'react';
import styled from 'styled-components';

interface CheckboxProps extends InputHTMLAttributes<HTMLInputElement> {
  checked?: boolean;
  label?: string;
}

const StyledInput = styled.input`
    display: none;
`;

const StyledCheckbox = styled.label`
    display: inline-flex;
    flex-direction: row;
    align-items: center;
    cursor: pointer;
    &:before {
        width: 16px;
        height: 16px;
        display: block;
        background-color: var(--color-secondary2);
        border-radius: var(--border-radius);
        content: '';
    }
`;

const StyledLabel = styled.span`
    margin-left: var(--units-medium);
`;

const Checkbox: FunctionComponent<CheckboxProps> = ({className, label, disabled, onChange, checked, ...rest}) => {
    const [isChecked, setIsChecked] = useState(!!checked);
    const inputEl = useRef(null);

    useEffect(() => {
        setIsChecked(checked)
        console.log(checked)
    }, [checked])

    const handleChange = e => {
        if (checked === undefined) {
            setIsChecked(e.target.checked)
        }
        if (onChange) {
          return onChange(e)
        }
    }

    const triggerChange = (): void => {
        inputEl.current.click();
    }

    return (
        <StyledCheckbox className={className} onClick={triggerChange}>
            <StyledInput type="checkbox" checked={checked !== undefined ? checked : isChecked} disabled={disabled} onChange={handleChange} ref={inputEl} {...rest} />
        {label && <StyledLabel>{label}</StyledLabel>}
        </StyledCheckbox>
    )
};

export default Checkbox;
