import React, { HTMLAttributes, useState, forwardRef, useEffect } from 'react';
import styled from 'styled-components';

interface CheckboxProps extends HTMLAttributes<HTMLInputElement> {
  checked?: boolean;
  label?: string;
}

const StyledCheckbox = styled.label`
`;

const StyledLabel = styled.span`
`;

const Checkbox = forwardRef(({ className, onChange, disabled, checked, label, ...rest }, ref) => {
    const [isChecked, setIsChecked] = useState(!!checked);

    useEffect(() => {
        setIsChecked(checked)
    }, [checked])

    const handleChange = e => {
        if (checked === undefined) {
            setIsChecked(e.target.checked)
        }
        if (onChange) {
          return onChange(e)
        }
      }

    return (
        <StyledCheckbox>
            <input type="checkbox" checked={checked !== undefined ? checked : isChecked} disabled={disabled} onChange={handleChange} {...rest} ref={ref} />
        {label && <StyledLabel>{label}</StyledLabel>}
        </StyledCheckbox>
    )
});

export default Checkbox;
